import logo from './logo.svg';
import './App.css';
import React, { useState, useRef, useEffect } from 'react'
import bell from './bell.wav'

const App = () => {

  const Ref = useRef(null);

  const [timer, setTimer] = useState('00:00:00');
  const [target, setTarget] = useState(10);
  const [remLoops, setRemLoops] = useState(1);

  const playSound = (url) => {
    const audio = new Audio(bell);
    audio.play();
  }

  const getTimeRemaining = (e) => {
      const total = Date.parse(e) - Date.parse(new Date());
      const seconds = Math.floor((total / 1000) % 60);
      const minutes = Math.floor((total / 1000 / 60) % 60);
      const hours = Math.floor((total / 1000 / 60 / 60) % 24);
      return {
          total, hours, minutes, seconds
      };
  }

  const showTimer = (hours, minutes, seconds) => {
    return (hours > 9 ? hours : '0' + hours) + ':' +
      (minutes > 9 ? minutes : '0' + minutes) + ':'
      + (seconds > 9 ? seconds : '0' + seconds)
  }

  const startTimer = (e) => {
      let { total, hours, minutes, seconds } 
                  = getTimeRemaining(e);
      if (total >= 0) {
          setTimer(
              showTimer(hours, minutes, seconds)
          )
          if (total === 0) {
              setRemLoops(l => l + 1);
              clearTimer(getDeadTime());
              playSound();
          }
      }
  }

  const stopTimer = () => {

    if (Ref.current) clearInterval(Ref.current);
    Ref.current = null;

  }

  const clearTimer = (e) => {

      if (Ref.current) clearInterval(Ref.current);
      const id = setInterval(() => {
          startTimer(e);
      }, 1000)
      Ref.current = id;
  }

  const getDeadTime = () => {
      let deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + target);
      return deadline;
  }

  const onClickStart = () => {
    setRemLoops(1);
      clearTimer(getDeadTime());
  }

  const onClickStop = () => {
      stopTimer();
  }

  return (
      <div className="App">
          <h1 className="Timer">{remLoops}</h1>
          <h1 className="Timer">{timer}</h1>
          <button className="Button start" onClick={onClickStart}>Start</button>
          <button className="Button stop" onClick={onClickStop}>Stop</button>

      </div>
  )
}

export default App;
